--  DDL for Procedure GESTDEPART
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "C_GPI"."GESTDEPART" IS
    V_MATRICULE EFFECTIF.MATRICULE%TYPE;
BEGIN
    -- Sélection automatique de l'employé démissionnaire
    SELECT MATRICULE
    INTO V_MATRICULE
    FROM EFFECTIF
    WHERE MATRICULE = (SELECT MAX(MATRICULE) FROM EFFECTIF WHERE STATUT_EMP='i');
      
    -- Transfert de l'employé sélectionné vers la table DEPART
    INSERT INTO DEPART (MATRICULE, MOBILE, NUMSECUSOC, DATERECRU, NATCONTRAT, NOM, PRENOM, TYPE_JOB, DEP_ID, GROUPSOC, DR_ID, USER_MAIL, DATEDEPART)
    SELECT MATRICULE, MOBILE, NUMSECUSOC, DATERECRU, NATCONTRAT, NOM, PRENOM, TYPE_JOB, DEP_ID, GROUPSOC, DR_ID, USER_MAIL, SYSDATE
    FROM EFFECTIF
    WHERE MATRICULE = V_MATRICULE;
    
    -- Suppression de l'employé transféré de la table EFFECTIF
    DELETE FROM EFFECTIF WHERE MATRICULE = V_MATRICULE AND STATUT_EMP='i';
    
    EXCEPTION
    WHEN NO_DATA_FOUND THEN
        DBMS_OUTPUT.PUT_LINE('Aucun employé démissionnaire trouvé.');
    WHEN OTHERS THEN
        DBMS_OUTPUT.PUT_LINE('Une erreur est survenue lors du transfert du personnel démissionnaire.');


END;

