      FROM DEPARTEMENTS) loop
         v_result.extend;
         v_result(v_result.count):=t_dep_row(rec.DEP_ID,rec.DEPARTEMENT,rec.NOM_DEPARTEMENT, rec.DR_ID);        
    end loop ;
